    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        /*String aux1 = N1.getText();
        String aux1 = N2.getText();*/
        
        if(!N1.getText().equals("") && !N2.getText().equals("")){
        int v1 = Integer.parseInt(N1.getText());
        int v2 = Integer.parseInt(N2.getText());
 
        int soma = v1+v2;
        
        resultado.setText(""+soma);
        }else
            resultado.setText("Valor incorreto, digite novamente!");
         
    }//GEN-LAST:event_jButton1ActionPerformed

